/* RADIO BUTTON STYLE */
.location {
  align-self: end;
  font-size: 1.8rem;
  grid-row: 4/5; }

.countries {
  grid-row-start: 5; }

.countries2 {
  grid-row-start: 6; }

/* Hide the browser's default radio button */
label {
  display: block;
  position: relative;
  font-size: 1.8rem;
  font-family: 'Oswald';
  font-weight: 400;
  color: white;
  cursor: pointer;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none; }
  label input {
    position: absolute;
    opacity: 0;
    cursor: pointer; }
  label h3 {
    position: absolute;
    z-index: 10;
    width: 100%;
    padding: 8%;
    text-align: center;
    font-size: 1.8rem; }

.checkmark {
  position: absolute;
  top: 0;
  left: 0;
  width: 90%;
  height: 90%;
  margin: 5%;
  background-color: #11514f;
  border-radius: 3%; }

/* On mouse-over, add a grey background color */
label:hover input ~ .checkmark {
  background-color: rgba(6, 39, 32, 0.3); }

/* When the radio button is checked, add a orange background */
label input:checked ~ .checkmark {
  width: 90%;
  height: 90%;
  background-color: #e1ac4a;
  text-align: center;
  border-radius: 5%;
  box-shadow: 5px 5px rgba(112, 81, 28, 0.6); }
  label input:checked ~ .checkmark h2 {
    font-size: 2.1rem;
    font-family: 'Oswald';
    font-weight: 400;
    color: white;
    padding: 2%; }

/* CHECKBOX STYLE */
.recurrent {
  align-self: end; }

.checkbox {
  position: absolute;
  top: 0;
  right: 5%;
  height: 25px;
  width: 25px;
  background-color: #11514f; }

/* On mouse-over, add a background color */
label:hover input ~ .checkbox {
  background-color: rgba(6, 39, 32, 0.3); }

/* When the checkbox is checked, add a background */
label input:checked ~ .checkbox {
  width: 25px;
  height: 25px;
  background-color: #e1ac4a;
  text-align: center;
  border-radius: 5%;
  box-shadow: 5px 5px rgba(112, 81, 28, 0.6); }
  label input:checked ~ .checkbox h2 {
    font-size: 2.1rem;
    font-family: 'Oswald';
    font-weight: 400;
    color: white;
    padding: 2%; }

/* Create the checkmark/indicator (hidden when not checked) */
.checkbox:after {
  content: "";
  position: absolute;
  display: none; }

/* Show the checkmark when checked */
label input:checked ~ .checkbox:after {
  display: block; }

/* Style the checkmark/indicator */
label .checkbox:after {
  left: 9px;
  top: 5px;
  width: 5px;
  height: 10px;
  border: solid white;
  border-width: 0 3px 3px 0;
  -webkit-transform: rotate(45deg);
  -ms-transform: rotate(45deg);
  transform: rotate(45deg); }

/* SUBMIT BUTTON */
.plant-submit {
  text-align: center;
  width: 90%;
  height: 90%;
  background-color: #e1ac4a;
  text-align: center;
