/* colors: */
* {
  box-sizing: border-box;
  margin: 0; }

body {
  width: 100%;
  height: 100%;
  margin: 0;
  padding: 0;
  background-color: #e0e4bd; }

/* 1 rem = 10 px */
:root {
  font-size: 62.5%; }

.getstarted {
  width: 300px;
  height: 45px;
  background-color: #e1ac4a;
  text-align: center;
  border-radius: 5%;
  box-shadow: 5px 5px rgba(112, 81, 28, 0.6);
  margin-left: calc(50% - 150px); }
  .getstarted h2 {
    font-size: 2.1rem;
    font-family: 'Oswald';
    font-weight: 400;
    color: white;
    padding: 2%; }

/* MENU HEADER */
header {
  width: 100vw;
  height: 100vh;
  padding: 1% 3%;
  background-image: url(47371ba35dbeb4e929118e8a151c52ea.svg);
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover; }

.menu {
  width: 90%;
  height: 10%;
  float: right;
  padding: 2%; }
  .menu li {
    display: inline; }
    .menu li .menu_link, .menu li .menu_link_right {
      font-size: 1.5rem;
      font-family: 'Oswald';
      font-weight: 400;
      color: #11514f;
      width: fit-content;
      padding-left: 5%; }
    .menu li .menu_link_right {
      float: right; }

svg #logo path {
  fill: #126864; }

/* PROCESS EXPLANATIONS */
.process_infos {
  margin: 15% 10% 7% 10%;
  width: 80%;
  height: 30%;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr; }
  .process_infos .cercle {
    margin: 15% calc(50% - 100px);
    width: 200px;
    height: 200px;
    border-radius: 50%;
    background-color: #cdcd55;
    box-shadow: 3px 3px rgba(30, 62, 30, 0.4); }
    .process_infos .cercle h2 {
      font-size: 2.8rem;
      font-family: 'Amatic Sc';
      font-weight: 700;
      color: white;
      margin: 0;
      width: 100%;
      height: 100%;
      padding: 25% 15%;
      text-align: center; }

/* LOGIN */
.login-form-modal, .register-form-modal {
  display: none; }

/* CONTENT */
main {
  background-color: #062720;
  width: 100vw;
  height: auto;
  padding: 1% 3% 10% 3%; }
  main h2 {
    font-size: 2.8rem;
    font-family: 'Amatic Sc';
    font-weight: 700;
    color: white;
    grid-column: -1/1; }
  main h3 {
    font-size: 2.1rem;
    font-family: 'Oswald';
    font-weight: 400;
    color: white;
    grid-column: -1/1; }
  main p {
    font-size: 1.8rem;
    font-family: 'Oswald';
    font-weight: 400;
    color: white; }

.content {
  margin-top: 15%;
  margin-left: 10%;
  margin-right: 10%;
  width: 80%;
  height: 30%;
  display: grid; }
  .content .forest-button {
    background-color: #b7decd;
    width: 100%;
    height: 500px;
    border-radius: 3%; }
    .content .forest-button a {
      font-size: 2.5rem;
      font-family: 'Amatic Sc';
      font-weight: 700;
      color: white; }

.forest-menu {
  grid-template-columns: 1fr 1fr 1fr 1fr;
  grid-gap: 2%; }

.why-info, .forest-content {
  grid-template-columns: 1fr 1fr;
  grid-gap: 5%; }
